import React, { ReactNode } from 'react';
import ReactDOM from 'react-dom';

class Item {
  text:string;
  id:number;
  constructor(public str:any, public iden:any){
    this.text=str;
    this.id=iden;
  }
}
class Statement{
  items: Array<Item>;
  text:string;
  constructor(public arr: Array<Item>, public str:string){
    this.items=arr;
    this.text=str;
  }
}
class ProgressApp extends React.Component {
  constructor(props : any) {
    super(props);
    this.setState(new Statement([], ''));
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3>TODO</h3>
        <TodoList items={(this.state as Statement).items} />
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="new-todo">
            What needs to be done?
          </label>
          <input
            id="new-todo"
            onChange={this.handleChange}
            value={(this.state as Statement).text}
          />
          <button>
            Add #{(this.state as Statement).items.length + 1}
          </button>
        </form>
      </div>
    );
  }

  handleChange(e : any) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e : any) {
    e.preventDefault();
    if ((this.state as Statement).text.length === 0) {
      return;
    }
    const newItem = new Item(
      (this.state as Statement).text,
      Date.now()
    );
    this.setState(state => ({
      items: (state as Statement).items.concat(newItem),
      text: ''
    }));
  }
}

class TodoList extends React.Component {
  render() {
    return (
      <ul>
        {(this.props as ReactNode).items.map(item => (
          <li key={item.id}>{item.text}</li>
        ))}
      </ul>
    );
  }
}

ReactDOM.render(
<ProgressApp />,
  document.getElementById('todos-example')
);
