import React, { ReactNode } from 'react';
import ReactDOM from 'react-dom';

class Item {
  text:string;
  id:number;
  constructor(public str:any, public iden:any){
    this.text=str;
    this.id=iden;
  }
}
class Statement{
  items: Array<Item>;
  text:string;
  constructor(public arr: Array<Item>, public str:string){
    this.items=arr;
    this.text=str;
  }
}

interface Props {
  width:number | null | string;
  height: number | null | string ;
  value: number;
  max: number;
  min: number | null;
  text: CallableFunction | null;
}

export class ProgressBar extends React.Component<Props,object> {
  static defaultProps={
    width : 'inherit',
    height : 'inherit',
    min : 0
  };

  constructor(props : any) {
    super(props);
    this.state= new Statement(new Array<Item>(), '');
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    const pBstyle={
      width : this.props.width?.toString(),
      height : this.props.height?.toString(),
    };
    const pVstyle={
      width : this.props.value+'%',
      height : this.props.height?.toString(),
      color : 'green'
    };
    return (

      <div>

        <div style={pBstyle} >
          <div style={pVstyle}></div>
        </div>
      </div>

    );
  }

  handleChange(e : any) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e : any) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    const newItem = new Item(
      this.state.text,
      Date.now()
    );
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}


export default ProgressApp;
