import React, { ReactNode } from 'react';
import ReactDOM from 'react-dom';
import { isNullOrUndefined } from 'util';



interface Props {
  width:  number | null | string;
  height: number | null | string ;
  value:  number;
  max:    number;
  min:    number | null;
  text:   Function | null;
}

class ProgressBar extends React.Component<Props,object> {

  constructor(props : any) {
    super(props);
  }

  render() {
    const pBstyle={
      width:  this.props.width?.toString(),
      height: this.props.height?.toString(),
    };
    const pVstyle={
      width:  this.props.value+'%',
      height: this.props.height?.toString(),
      backgroundColor : 'green',
      textAlign : 'center'
    };
    return (

        <div style={pBstyle} >
          <div style={pVstyle}>{this.props.text?.call(new Object(),[])}</div>
        </div>

    );
  }
}
export class ProgressApp extends React.Component<Props,object> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : function () : string {return '10'}
  };

  constructor(props : any) {
    super(props);
  }
  render(){
    return (

      <ProgressBar  max={this.props.max}
      value={this.props.value}
      width= {this.props.width}
      height= {this.props.height}
      min= {this.props.min}
      text= {this.props.text}
      />
    );
  }

}


export default ProgressApp;
