import React, { ReactNode } from 'react';
import * as CSS from 'csstype';



interface Props {
  width:  number | null | string;
  height: number | null | string ;
  value:  number;
  max:    number;
  min:    number | null;
  text:   (argArray : any[]) => string;
}

interface Statement {
  interval : NodeJS.Timeout;
  value : number;
}

class ProgressBar extends React.Component<Props,object> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : function () : string {return ''}
  };
  constructor(props : any) {
    super(props);
  }

  render() {
    const pBstyle={
      width:  this.props.width?.toString(),
      height: this.props.height?.toString(),
      backgroundColor: '#ddd'
    };

    const pVstyle :  CSS.Properties =  {
      width:  this.props.value+'%',
      height: this.props.height?.toString(),
      backgroundColor : 'green',
      textAlign : 'center',
      paddingTop: '0.3em'
    };
    return (

        <div style={pBstyle} >
          <div style={pVstyle}>{this.props.text.call(new Object(),[])}</div>
        </div>

    );
  }
}

export class ProgressApp extends React.Component<Props, Statement> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : function () : string {return '50'}
  };

  constructor(props : any) {
    super(props);
    this.state={interval: setInterval(() => this.tick(), 1000), value: this.props.value};
  }

  tick() {
    this.setState({value: this.state.value+1});
  }

  timerStop() {
    clearInterval(this.state.interval);

  }

  render(){
    return (
      <div>
        <button onClick={this.timerStop}>Stop</button>
        <ProgressBar  max={this.props.max}
        value={this.state.value}
        />
      </div>
    );
  }

}


export default ProgressApp;
