import React, { ReactNode } from 'react';
import ReactDOM from 'react-dom';
import { isNullOrUndefined } from 'util';



interface Props {
  width:  number | null | string;
  height: number | null | string ;
  value:  number;
  max:    number;
  min:    number | null;
  text:   Function;
}

class ProgressBar extends React.Component<Props,object> {

  constructor(props : any) {
    super(props);
  }

  render() {
    const pBstyle={
      width:  this.props.width?.toString(),
      height: this.props.height?.toString(),
    };
    const pVstyle={
      width:  this.props.value+'%',
      height: this.props.height?.toString(),
      backgroundColor : 'green'
    };
    return (

        <div style={pBstyle} >
          <div style={pVstyle}></div>
          <p style={{display: 'inline'}}>{String(this.props.text().value)}</p>
        </div>

    );
  }
}
export class ProgressApp extends React.Component<Props,object> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : ()=>{ return{
      value: ''
    }}
  };

  constructor(props : any) {
    super(props);
  }
  render(){
    return (
      <ProgressBar  max={100}
      value={52}
      width='inherit'
      height= 'inherit'
      min= {0}
      text={async () => {
        return {
          value : '258'
        }
      }}/>
    );
  }

}


export default ProgressApp;
