import React, { Component, MouseEvent } from 'react';
import * as CSS from 'csstype';



interface Props {
  width:  number | null | string;
  height: number | null | string ;
  value:  number;
  max:    number;
  min:    number | null;
  text:   (argArray : any[]) => string;
}

interface Statement {
  interval :  NodeJS.Timeout;
  value :     number;
  isTimerOn : boolean;
}

class ProgressBar extends Component<Props,object> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : ([])=> ''
  };
  constructor(props : any) {
    super(props);
  }

  render() {
    const pBstyle={
      width:  this.props.width?.toString(),
      height: this.props.height?.toString(),
      backgroundColor: '#ddd'
    };

    const pVstyle :  CSS.Properties =  {
      width:  this.props.value/this.props.max * 100+'%',
      height: this.props.height?.toString(),
      backgroundColor : 'green',
      textAlign : 'center'
    };
    return (

        <div style={pBstyle} >
          <div style={pVstyle}>{this.props.text?.call(new Object(),[this.props.value])}</div>
        </div>

    );
  }
}

export class ProgressApp extends Component<Props, Statement> {

  static defaultProps={
    width:  'inherit',
    height: 'inherit',
    min : 0,
    text : ([]) => ''
  };

  constructor(props : any) {
    super(props);
    this.state={ interval : setInterval(() => this.tick(), 100) ,value: this.props.value,
       isTimerOn : true};
    this.stopTimer=this.stopTimer.bind(this);
    this.startTimer=this.startTimer.bind(this);
    this.tick=this.tick.bind(this);
  }

  tick() {
    //alert("by" + this.state.value);
    if(this.state.value<this.props.max)
    {
      this.setState({value: this.state.value+1});
      //alert("by" + this.state.value);
    }
    else
      clearInterval(this.state.interval);
  }

  stopTimer(event : MouseEvent<HTMLButtonElement>) {
    this.setState({isTimerOn: false});
    clearInterval(this.state.interval);
    //event.target.addEventListener('click', this.startTimer);

  }

  startTimer(){
    //alert("hello");
    this.setState({interval: setInterval(() => this.tick(), 100), isTimerOn : true});
  }

  resetTimer(){
    if(!this.state.isTimerOn){
      this.setState({interval: setInterval(() => this.tick(), 100),isTimerOn : true});
    }
    this.setState({value: this.props.min||0});
  }

  render(){
    const divStyle :  CSS.Properties =  {
      paddingLeft : '1em',
      paddingTop : '1em',
      paddingRight : '1em'
    };

    let start = (!this.state.isTimerOn) ?
      <button onClick={this.startTimer}>Start</button> :
      null
    let stop = (this.state.isTimerOn) ?
      <button onClick={this.stopTimer}>Stop</button> :
      null
    let reset = <button onClick={this.resetTimer}>Reset</button>


    return (

      <div style={divStyle}>

        <ProgressBar  max={this.props.max}
        value={this.state.value}
        text={this.props.text}
        />
        <br/>
        {start}
        {stop}
      </div>
    );
  }

}


export default ProgressApp;
